mapper.select:
SELECT *
FROM
member
WHERE name = #{name}
AND email = #{email}

SELECT *
FROM
member
WHERE loginId = #{loginId}
AND email = #{email}

mapper.update:
UPDATE member
SET loginPw = SHA2(#{newPw}, 256)
WHERE loginId = #{loginId}

UPDATE member
SET AuthStatus = 1
WHERE email = #{email}

UPDATE member
SET profile_text = #{profile_text}
WHERE id = #{id}
