mapper.update:
UPDATE article
updateDate = NOW(), title = #{title}, body = #{body},
WHERE id = #{id}

mapper.select:

SELECT AR.*,
M.nickname AS extra__writer
FROM reply AS AR
INNER JOIN member AS M
ON AR.memberId = M.id
WHERE AR.relId = #{relId}
AND AR.relTypeCode = #{relTypeCode}
AND AR.displayStatus = 1
AND AR.id >= #{from}
ORDER BY AR.id ASC
